import 'dart:ui';import 'package:flutter/material.dart';import '../utils/style.dart';import 'custom_button.dart';class AcceptAlertDialog extends StatelessWidget {  final Function onAccept;  final Widget title;  final String text;  final String textButton;  const AcceptAlertDialog(      {super.key,      required this.onAccept,      this.title = const SizedBox(),      required this.text,      required this.textButton});  @override  Widget build(BuildContext context) {    return AlertDialog(      actionsPadding: const EdgeInsets.symmetric(horizontal: 22,vertical: 22),      shape: const RoundedRectangleBorder(          borderRadius: BorderRadius.all(Radius.circular(10.0))),      title: title,      content: Text("$text",          textAlign: TextAlign.center,          style: const TextStyle(              fontWeight: FontWeight.w300,              fontSize: 16,              fontFamily: AppFont.heavy,              color: Color.fromRGBO(0, 0, 0, 1))),      actions: <Widget>[        CustomButton(          height: 42,          text: textButton,          onTap: () {},        ),      ],    );  }}